use super::*;

#[test]
fn test_2024_06_part_1_small() {
    let result = part1(INPUT_SMALL.split("\n").collect());
    assert_eq!(result, 41);
}

#[test]
fn test_2024_06_part_1_big() {
    let result = part1(INPUT.split("\n").collect());
    assert_eq!(result, 5030);
}

#[test]
fn test_2024_06_part_2_small() {
    let result = part2(INPUT_SMALL.split("\n").collect());
    assert_eq!(result, 6);
}

#[test]
fn test_2024_06_part_2_big() {
    let result = part2(INPUT.split("\n").collect());
    assert_eq!(result, 1928);
}

